            }

            if ((parseInt(this.user[this.index].TrackerDistance) / 2) != 0) {
              this.data.push(this.getTrackerDistanceScore((parseInt(this.user[this.index].TrackerDistance) / (1440 - (5 * i + this.random(500))))));
            } else {
